INSERT INTO `wp_posts` VALUES ('156', '1', '2017-01-17 17:09:33', '2017-01-17 09:09:33', '\r\n  <div class=\"service-salary-management\">\r\n    <div class=\"service-profile\">\r\n      <div class=\"page-min-width-container\">\r\n        <div class=\"paragraph\" style=\"width: 1080px\">\r\n<h2 class=\"text-green\">Visa Sponsorship in Asia</h2><br><br>Are you looking to employ overseas or expatriate staff in Asia?<br>\r\n\r\nTake our 1 minute visa self-assessment now to find out whether your overseas or expatriate staff are likely to receive an employment visa.<br>\r\n\r\nLinks International provides visa advice in respect of immigration in Asia.  Our professional staff have years of experience dealing with local immigration authorities and ensure efficient turnaround of  your employee\'s visa application.<br>\r\n        </div>\r\n      </div>\r\n    </div>\r\n    <div class=\"service-content\">\r\n        <div class=\"page-min-width-container clearfix\">\r\n          <img src=\"resource/images/service_img.jpg\" alt=\"\">\r\n          <div class=\"float-right\">\r\n<h2>Services</h2>\r\n            <ul class=\"service-item\">\r\n              <li>Visa self-assessment</li>\r\n            </ul>\r\n          </div>\r\n        </div>\r\n      </div>\r\n  </div>', '工作签证申办', '', 'inherit', 'closed', 'closed', '', '67-revision-v1', '', '', '2017-01-17 17:09:33', '2017-01-17 09:09:33', '', '67', 'http://10.10.10.224/wordpress/2017/01/17/67-revision-v1/', '0', 'revision', '', '0');
